mapper.update:
	update tb_account
  set LAST_LOGIN_TIME = #{lastLoginTime,jdbcType=TIMESTAMP},
    LAST_LOGIN_IP = #{lastLoginIp,jdbcType=VARCHAR}
  where ACCOUNT_ID = #{accountId,jdbcType=INTEGER}

mapper.select:
select
from tb_account
AND (LOGIN_NAME LIKE '%' #{searchContent} '%' OR USER_NAME LIKE '%' #{searchContent} '%' OR LAST_LOGIN_IP LIKE '%' #{searchContent} '%' OR EMAIL LIKE '%' #{searchContent} '%' OR TELEPHONE LIKE '%' #{searchContent} '%')
AND CREATE_DATE >= #{beginCreateTime}
AND CREATE_DATE <= #{endCreateTime}

	select

	from tb_account


        and ACCOUNT_ID = #{accountId,jdbcType=DECIMAL}


       and LOGIN_NAME = #{loginName,jdbcType=VARCHAR}


       and USER_NAME = #{userName,jdbcType=VARCHAR}


       and EMAIL = #{email,jdbcType=VARCHAR}


       and TELEPHONE = #{telephone,jdbcType=VARCHAR}


       and LOGIN_PASSWORD = #{loginPassword,jdbcType=VARCHAR}

mapper.update:
update tb_account
set LOGIN_PASSWORD = #{loginPassword,jdbcType=VARCHAR}
where ACCOUNT_ID = #{accountId,jdbcType=INTEGER}

update tb_account 
set PIC_IMG = #{picImg,jdbcType=VARCHAR}
where ACCOUNT_ID = #{accountId,jdbcType=INTEGER}

update tb_account
set STATUS = #{status,jdbcType=INTEGER} 
where ACCOUNT_ID = #{accountId,jdbcType=INTEGER}
